export default {
  data: function data() {
    return {
      apply: {}
    };
  },
  created: function created() {
    if (this.$route.query.type != "2") {
      this.apply.type = "0";
    }
  },
  methods: {
    onSubmit: function onSubmit() {
      var _this = this;

      var empId = window.sessionStorage.getItem('empId');
      this.apply.startTime = this.apply.startTime.toISOString().substring(0, 10) + " " + this.apply.startTime.toISOString().substring(11, 19);
      console.log('applyVacation/' + empId + '/' + this.apply.startTime + '/' + this.apply.duringTime + '/' + this.apply.reason + '/' + this.apply.type);
      this.$http.post('applyVacation/' + empId + '/' + this.apply.startTime + '/' + this.apply.duringTime + '/' + this.apply.reason + '/' + this.apply.type).then(function (res) {
        if (res.data) {
          _this.$message({
            message: '添加成功',
            type: 'success'
          });

          _this.$router.push('/blogDetail');
        } else {
          _this.$message({
            message: '添加失败',
            type: 'error'
          });
        }
      }); // this.$http.get('queryAllEmployeeInfo').then(res => {
      //   console.log(res);
