export default {
  data: function data() {
    return {
      page: []
    };
  },
  created: function created() {
    this.loaddata();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var empId = window.sessionStorage.getItem('empId');
      this.$http.get('/getApplyRecordById/' + empId).then(function (res) {
        _this.page = res.data;
      });
    },
    formatState: function formatState(row) {
      if (row.state == 0) {
        return '拒绝';
      } else if (row.state == 1) {
        return '审核中';
      } else if (row.state == 2) {
        return '通过';
      }
    }
  }
};
