export default {
  data: function data() {
    return {
      page: []
    };
  },
  created: function created() {
    this.loaddata();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var empId = window.sessionStorage.getItem('empId');
      this.$http.get('/getApplyRecordById/' + empId).then(function (res) {
        _this.page = res.data;
      });
    },
    formatState: function formatState(row) {
      if (row.state == 0) {
        return '拒绝';
      } else if (row.state == 1) {
        return '审核中';
      } else if (row.state == 2) {
        return '通过';
      }
    },
    formatType: function formatType(row) {
      if (row.type == 0) {
        return '事假';
      } else if (row.type == 1) {
        return '年假';
      } else if (row.type == 2) {
        return '婚假';
      } else if (row.type == 3) {
        return '产检';
      } else if (row.type == 4) {
        return '产假';
      } else if (row.type == 5) {
        return '哺乳';
      } else if (row.type == 6) {
        return '陪产';
      } else if (row.type == 7) {
        return '外出';
      }
    }
  }
};
