export default {
  data: function data() {
    return {
      page: [],
      state: 0
    };
  },
  created: function created() {
    this.loaddata();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var account = window.sessionStorage.getItem('account');
      this.$http.get('/queryRecordsByActOrType?account=' + account).then(function (res) {
        console.log(res.data);
        _this.page = res.data;
      });
    },
    loadstate: function loadstate() {
      var _this2 = this;

