      this.$http.post('/checkInOrOut/' + empId + '/' + num).then(function (res) {
        console.log(res.data);

        if (res.data) {
          _this3.$message({
            message: '操作成功',
            type: 'success'
          });

          _this3.loaddata();
        } else {
          _this3.$message({
            message: '操作失败',
            type: 'error'
          });
        }
      });
    }
  }
