export default {
  setup: function setup() {
    this.data = {
      page: []
    };
  },
  created: function created() {
    this.loaddata();
  },
  methods: {
    loaddata: function loaddata() {
      var _this = this;

      var account = window.sessionStorage.getItem('account');
      this.$http.get('/queryAllRecords').then(function (res) {
        console.log(res.data);
        _this.page = res.data;
      });
    },
    formatType: function formatType(row) {
      if (row.type == 0) {
        return '上班打卡';
      } else if (row.type == 1) {
        return '下班打卡';
      } else if (row.type == 2) {
        return '外出打卡';
      } else if (row.type == 3) {
        return '返回打卡';
      }
    },
    formatDate: function formatDate(row) {
      var date = new Date(row.time);
      var Y = date.getFullYear() + '-';
      var M = (date.getMonth() + 1 < 10 ? '0' + (date.getMonth() + 1) : date.getMonth() + 1) + '-';
      var D = date.getDate() + ' ';
      var h = date.getHours() + ':';
      var m = date.getMinutes() + ':';
      var s = date.getSeconds();
      return Y + M + D + h + m + s;
    }
  }
};
